pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./Layer2Readable.sol";

// This interface represents the functions we are "virtually" supporting via the L2 interface.
interface Claimable {
    function claim(address addr) external;
    function claimableBalance(address addr) external view returns(uint);
}

contract MerkleToken is ERC20, Layer2Readable {
    bytes32 public merkleRoot;
    string[] proxies;
    mapping(uint=>uint) claimed;

    constructor(bytes32 root, string[] memory _proxies) ERC20("Merkle token", "MERKLE") public {
        merkleRoot = root;
        proxies = _proxies;
    }

    function getProxyURLs(bytes4 functionId) external override view returns (bytes4 callbackId, string[] memory urls) {
        if(functionId == Claimable.claim.selector) {
            return (MerkleToken.claim.selector, proxies);
        } else if(functionId == Claimable.claimableBalance.selector) {
            return (MerkleToken.claimableBalance.selector, proxies);
        }
    }

    function claimableBalance(address addr, uint balance, bytes memory proof) external view returns(uint) {
        (bool valid, uint idx) = checkProof(proof, merkleRoot, keccak256(abi.encodePacked(addr, balance)));
        require(valid, "Invalid merkle proof");
        if(claimed[idx >> 8] & (1 << (idx & 0xff)) == 0) {
            return balance;
        }
        return 0;
    }

    function claim(address addr, uint balance, bytes memory proof) external {
        (bool valid, uint idx) = checkProof(proof, merkleRoot, keccak256(abi.encodePacked(addr, balance)));
        require(valid, "Invalid merkle proof");
        if(claimed[idx >> 8] & (1 << (idx & 0xff)) == 0) {
            claimed[idx >> 8] |= 1 << (idx & 0xff);
            _mint(addr, balance);
        }
    }

    // Adapted from https://github.com/ameensol/merkle-tree-solidity/blob/master/src/MerkleProof.sol
    function checkProof(bytes memory proof, bytes32 root, bytes32 hash) internal pure returns (bool, uint) {
        bytes32 el;
        bytes32 h = hash;
        uint idx = 0;

        for (uint256 i = 32; i <= proof.length; i += 32) {
            assembly {
                el := mload(add(proof, i))
            }

            idx <<= 1;
            if (h < el) {
                h = keccak256(abi.encodePacked(h, el));
            } else {
                h = keccak256(abi.encodePacked(el, h));
                idx |= 1;
            }
        }

        return (h == root, idx);
    }
}
